#Functions
def filter_list_school_type(school_type):
    if school_type == "public":
        for i in range(0, len(private_yes_or_no)):
            if private_yes_or_no[i] == "Yes":
                if college_names[i] in filtered_list:
                 filtered_list.remove(college_names[i])

    if school_type == "private":
        for i in range(0, len(private_yes_or_no)):
            if private_yes_or_no[i] == "No":
                if college_names[i] in filtered_list:
                    filtered_list.remove(college_names[i])

def filter_list_graduation_rate(graduation_rate):
    for i in range(0, len(graduation_rates)):
        if graduation_rates[i] < graduation_rate:
            if college_names[i] in filtered_list:
                filtered_list.remove(college_names[i])

def filter_list_tuition(tuition):
    for i in range(0, len(tuition_costs)):
        if tuition_costs[i] > tuition:
            if college_names[i] in filtered_list:
             filtered_list.remove(college_names[i])

def filter_list_freshman_population(freshman_population):
    if freshman_population == "large":
        for i in range(0, len(freshman_class_population)):
            if freshman_class_population[i] < 1000:
                if college_names[i] in filtered_list:
                    filtered_list.remove(college_names[i])

    if freshman_population == "small":
        for i in range(0, len(freshman_class_population)):
            if freshman_class_population[i] > 1000:
                if college_names[i] in filtered_list:
                    filtered_list.remove(college_names[i])

def show_overall_statistics():
    number_of_colleges = len(college_names)
    average_graduation_rate = sum(graduation_rates)/number_of_colleges
    average_tuition = sum(tuition_costs)/number_of_colleges
    average_freshman_population = sum(freshman_class_population)/number_of_colleges
    public_schools = private_yes_or_no.count("No")
    private_schools = private_yes_or_no.count("Yes")
    print("Although our list is big, we don't have every single college in the US. However, from the ones we have, the average graduation rate is " + str(round(average_graduation_rate)) + "%, the average tuition is $" + str(round(average_tuition)) + ", and the average freshman class population is " + str(round(average_freshman_population)) + ". Also, our list has " + str(public_schools) + " public schools and " + str(private_schools) + " private schools.")

def college_recommender():
    while b == 0:
        x = input("Welcome to the college recommender! What would you like to do? \n 1) Find out what our program does \n 2) Get an idea of the overall college statistics in our list \n 3) Look up information on a college \n 4) Get a college recommendation based on your preferences \n 5) Get a random recommendation \n 6) Exit the program" )
        if x == "1":
            print("We know that finding the perfect college can be a very long and stressful process. That's where we come in. We will ask you a few questions and get your preferences on school size, type of school, and a few more things. We will then filter through hundreds of colleges and give you a recommendation based on all of your preferences. We hope you find this tool useful in your search for your home for the next four years!")
        if x == "2":
            show_overall_statistics()
        elif x == "3":
            y = input("Which college do you want to look up information on?")
            for i in range(0, len(college_names)):
                if y == college_names[i]:
                    print("Let's take a look at " + y + "! " + y + " has a graduation rate of " + str(graduation_rates[i]) + "%" + ", a freshman class population of " + str(freshman_class_population[i]) + ", and tuition costs $" + str(tuition_costs[i]) + ".")
                    break
            else:
                print("Sorry, but that college isn't in our list. Try another one!")
        elif x == "4":
            filter_list_school_type(input("Do you want to go to a public or private college?"))
            filter_list_graduation_rate(int(input("We're sure you want to go to a school with good academics. With that said, what do you want the the minimum graduation rate you would want at your college?")))
            filter_list_tuition(int(input("We also know you don't want to spend a fortune on college. What is the maximum amount of money you'd like to pay for one year of tuition?")))
            filter_list_freshman_population(input("Now for the final step, we know that the size of the school matters as well. If you would like to go to a school with a freshman population over 1,000, respond with large. If you want to go to a school with a freshman population under 1,000, respond with small." ))
            if not filtered_list:
                print("Unfortunately, none of the colleges fit all of your preferences. Try again!")
            else:
                college = random.choice(filtered_list)
                print("Based on your preferences, we recommend you to go to " + college + ".")
        elif x == "5":
            random_college = random.choice(college_names)
            print("Feeling indecisive? Here's a random recommendation: You should go to " + random_college + ".")
        elif x == "6":
            print("Thanks for using this college recommendation program. See you next time!")
            break





#Main
college_recommender()

